{
  "story": "きのう、がっこうのあとで、ともだちとこうえんであそびました。いぬもつれてきて、たのしかったです。",
  "question": "どこであそびましたか？",
  "options": {
    "a": "がっこう",
    "b": "こうえん",
    "c": "いえ",
    "d": "みせ"
  },
  "correct_answer": "b"
}

{
  "story": "けさ、でんしゃがこんでいたので、ともだちといっしょにバスにのりました。がっこうにはまにあいました。",
  "question": "けさ、なににのりましたか？",
  "options": {
    "a": "じてんしゃ",
    "b": "でんしゃ",
    "c": "バス",
    "d": "くるま"
  },
  "correct_answer": "c"
}

{
  "story": "日よう日、ともだちとこうえんであそびました。サッカーをして、ねこともはしりました。",
  "question": "どこであそびましたか？",
  "options": {
    "a": "がっこう",
    "b": "いえ",
    "c": "こうえん",
    "d": "えき"
  },
  "correct_answer": "c"
}
